var browser = navigator.userAgent.toLowerCase();
        if (browser.indexOf("msie") != -1 && parseInt(browser.split("msie")[1]) <= 8)
        {
        // remove html on page
        document.body.innerHTML = "";
        
        
        // make the banner
        var logo = document.createElement("img");
        logo.src = "https://ccm.sickkids.ca/wp-content/uploads/2015/02/phenotips-logo-300x56.png";
        logo.height = "50";
        logo.id = "logo";
        var group = document.createElement("div");
        group.id = "container";
        
        
        group.appendChild(logo);
        
        // add text
        var text = document.createTextNode("It appears you are using Internet Explorer version 8 or below.\n"
        + "Please download a newer version or a different browser to continue using PhenoTips using the below links:");
        var pnode = document.createElement("p");
        pnode.appendChild(text);
        
        group.appendChild(pnode);
        
        document.body.appendChild(group);
        
        // add images
        var chromDiv = document.createElement("div");
        chromDiv.class = "imagecontainer";
        var chromeLogo = document.createElement("img");
        chromeLogo.id = "browserLogo";
        chromeLogo.src = "http://www.google.com/chrome/assets/common/images/chrome_logo_2x.png";
        var chromeLink = document.createElement("a");
        chromeLink.href = "https://www.google.com/chrome/browser/desktop/index.html";
        chromeLink.appendChild(chromeLogo);
        chromDiv.appendChild(chromeLink);
        
        var firefoxDiv = document.createElement("div");
        firefoxDiv.class = "imagecontainer";
        var firefoxLogo = document.createElement("img");
        firefoxLogo.id = "browserLogo";
        firefoxLogo.src = "https://mozorg.cdn.mozilla.net/media/img/firefox/new/header-firefox.98d0a02c957f.png";
        var firefoxLink = document.createElement("a");
        firefoxLink.href = "https://www.mozilla.org/en-us/firefox/new/";
        firefoxLink.appendChild(firefoxLogo);
        firefoxDiv.appendChild(firefoxLink);
        
        var ieDiv = document.createElement("div");
        ieDiv.class = "imagecontainer";
        var ieLogo = document.createElement("img");
        ieLogo.id = "browserLogo";
        ieLogo.src = "https://c.s-microsoft.com/en-ca/cmsimages/iebluee_0304_70x70_en_us.png?version=8cada7a1-74f4-ebb3-cce0-af842a79ada8";
        var ieLink = document.createElement("a");
        ieLink.href = "http://windows.microsoft.com/en-ca/internet-explorer/download-ie";
        ieLink.appendChild(ieLogo);
        ieDiv.appendChild(ieLink);
        
        var linkContainer = document.createElement("div");
        pnode.appendChild(linkContainer);
        var linkNode = document.createElement("div");
        chromDiv.id = "left";
        firefoxDiv.id = "middle";
        ieDiv.id = "right";
        linkNode.appendChild(chromDiv);
        linkNode.appendChild(firefoxDiv);
        linkNode.appendChild(ieDiv);
        linkContainer.appendChild(linkNode);
        }

